import React from "react";
import { RadialBarChart, RadialBar, Legend } from "recharts";

const data = [{ name: "SOC", value: 65, fill: "#8884d8" }];

const BatteryRadialBar = ({ soc }) => {
	const chartData = [
		{ name: "SOC", value: soc, fill: soc > 20 ? "#00C49F" : "#FF8042" },
	];
	return (
		<RadialBarChart
			width={150}
			height={150}
			cx={75}
			cy={75}
			innerRadius={20}
			outerRadius={70}
			barSize={10}
			data={chartData}
		>
			<RadialBar
				minAngle={15}
				background
				clockWise={true}
				dataKey="value"
			/>
			<Legend
				iconSize={10}
				layout="vertical"
				verticalAlign="middle"
				wrapperStyle={{
					top: "50%",
					left: "70%",
					transform: "translate(-50%, -50%)",
				}}
			/>
		</RadialBarChart>
	);
};

export default BatteryRadialBar;
