// src/components/VisualRepresentation.js
import React, { useState, useEffect } from "react";
import axios from "../api";
import "./VisualRepresentation.css";

const VisualRepresentation = () => {
	const [data, setData] = useState(null);

	useEffect(() => {
		const fetchLoadDistribution = async () => {
			try {
				const response = await axios.get("/load_distribution");
				setData(response.data);
			} catch (error) {
				console.error("Error fetching load distribution:", error);
			}
		};

		fetchLoadDistribution();
	}, []);

	if (!data) {
		return <div>Loading...</div>;
	}

	return (
		<div className="visual-representation">
			<div className="building">
				<h3>Building</h3>
				<p>Load: {data.System.building_load} kW</p>
			</div>
			<div className="battery">
				<h3>Battery</h3>
				<p>Capacity: {data.System.battery_capacity} kWh</p>
				<p>Available: {data.System.battery_capacity_available} kWh</p>
				<p>Diverted: {data.System.battery_capacity_diverted} kWh</p>
			</div>
			<div className="stations">
				{data.stations.map((station) => (
					<div key={station.station_id} className="station">
						<h4>Station {station.station_id}</h4>
						<p>Charge Rate: {station.charge_rate} kW</p>
						<p>Max Feasible Rate: {station.max_feasible_rate} kW</p>
						<p>Vehicle SOC: {station.vehicle_soc * 100}%</p>
						<p>
							Vehicle Max Charging Rate:{" "}
							{station.vehicle_max_charging_rate} kW
						</p>
						<p>Desired SOC: {station.vehicle_desired_soc * 100}%</p>
						<p>
							Estimated Charging Time:{" "}
							{station.estimated_charging_time} hours
						</p>
					</div>
				))}
			</div>
		</div>
	);
};

export default VisualRepresentation;
