import React, { useState, useEffect } from "react";
import axios from "../api";

const VehicleList = () => {
	const [vehicles, setVehicles] = useState([]);
	const [stations, setStations] = useState([]);
	const [selectedVehicle, setSelectedVehicle] = useState("");
	const [selectedStation, setSelectedStation] = useState("");

	useEffect(() => {
		const fetchVehicles = async () => {
			try {
				const response = await axios.get("/vehicles");
				setVehicles(response.data);
			} catch (error) {
				console.error("Error fetching vehicles:", error);
			}
		};

		const fetchStations = async () => {
			try {
				const response = await axios.get("/stations");
				setStations(response.data);
			} catch (error) {
				console.error("Error fetching stations:", error);
			}
		};

		fetchVehicles();
		fetchStations();
	}, []);

	const handleConnectVehicle = async () => {
		try {
			await axios.post("/connect_vehicle", {
				vehicle: selectedVehicle,
				station_id: selectedStation,
			});
			alert(
				`Vehicle ${selectedVehicle} connected to station ${selectedStation} successfully!`
			);
		} catch (error) {
			alert("Failed to connect vehicle.");
			console.error(error);
		}
	};

	const handleDisconnectVehicle = async (vehicleId) => {
		try {
			await axios.post("/disconnect_vehicle", { vehicle: vehicleId });
			alert(`Vehicle ${vehicleId} disconnected successfully!`);
		} catch (error) {
			alert("Failed to disconnect vehicle.");
			console.error(error);
		}
	};

	return (
		<div className="vehicle-list">
			<h2>Vehicles</h2>
			<ul>
				{vehicles.map((vehicle) => (
					<li key={vehicle.vehicle_id}>
						{vehicle.vehicle_id} - {vehicle.soc}%
						<button
							onClick={() =>
								handleDisconnectVehicle(vehicle.vehicle_id)
							}
						>
							Disconnect
						</button>
					</li>
				))}
			</ul>
			<div>
				<select
					value={selectedVehicle}
					onChange={(e) => setSelectedVehicle(e.target.value)}
				>
					<option value="">Select Vehicle</option>
					{vehicles.map((vehicle) => (
						<option
							key={vehicle.vehicle_id}
							value={vehicle.vehicle_id}
						>
							{vehicle.vehicle_id}
						</option>
					))}
				</select>
				<select
					value={selectedStation}
					onChange={(e) => setSelectedStation(e.target.value)}
				>
					<option value="">Select Station</option>
					{stations.map((station) => (
						<option
							key={station.station_id}
							value={station.station_id}
						>
							{station.station_id}
						</option>
					))}
				</select>
				<button onClick={handleConnectVehicle}>Connect Vehicle</button>
			</div>
		</div>
	);
};

export default VehicleList;
