// src/components/SystemConfig.js
import React, { useState } from "react";
import axios from "../api";

const SystemConfig = () => {
	const [internalCapacity, setInternalCapacity] = useState(100);
	const [batteryCapacity, setBatteryCapacity] = useState(100);

	const handleConfigSubmit = async (e) => {
		e.preventDefault();
		try {
			await axios.post("/configure_system", {
				internal_capacity: internalCapacity,
				battery_capacity: batteryCapacity,
			});
			alert("System configuration updated successfully!");
		} catch (error) {
			alert("Failed to update system configuration.");
			console.error(error);
		}
	};

	return (
		<div className="system-config">
			<h2>System Configuration</h2>
			<form onSubmit={handleConfigSubmit}>
				<label>
					Internal Capacity:
					<input
						type="number"
						value={internalCapacity}
						onChange={(e) =>
							setInternalCapacity(Number(e.target.value))
						}
					/>
				</label>
				<br />
				<label>
					Battery Capacity:
					<input
						type="number"
						value={batteryCapacity}
						onChange={(e) =>
							setBatteryCapacity(Number(e.target.value))
						}
					/>
				</label>
				<br />
				<button type="submit">Update Configuration</button>
			</form>
		</div>
	);
};

export default SystemConfig;
