// src/components/StationList.js
import React, { useState, useEffect } from "react";
import axios from "../api";

const StationList = () => {
	const [stations, setStations] = useState([]);
	const [newStationId, setNewStationId] = useState("");

	useEffect(() => {
		const fetchStations = async () => {
			try {
				const response = await axios.get("/stations");
				setStations(response.data);
			} catch (error) {
				console.error("Error fetching stations:", error);
			}
		};
		fetchStations();
	}, []);

	const handleAddStation = async () => {
		if (stations.length >= 5) {
			alert("Maximum number of stations reached (5).");
			return;
		}

		try {
			await axios.post("/add_station", { station_id: newStationId });
			setNewStationId("");
			alert(`Station ${newStationId} added successfully!`);
		} catch (error) {
			alert("Failed to add station.");
			console.error(error);
		}
	};

	return (
		<div className="station-list">
			<h2>Stations</h2>
			<ul>
				{stations.map((station) => (
					<li key={station.station_id}>
						{station.station_id}
						{/* Include other station details as needed */}
					</li>
				))}
			</ul>
			<div>
				<input
					type="text"
					placeholder="Enter station ID"
					value={newStationId}
					onChange={(e) => setNewStationId(e.target.value)}
				/>
				<button onClick={handleAddStation}>Add Station</button>
			</div>
		</div>
	);
};

export default StationList;
